import java.awt.Component;
import java.net.MalformedURLException;
import java.net.URL;

import javax.swing.Icon;
import javax.swing.ImageIcon;
import javax.swing.JFrame;
import javax.swing.JLabel;
import javax.swing.JOptionPane;

public class PhotoQuiz {

	public static void main(String[] args) throws Exception {
		JFrame quizWindow = new JFrame();
		quizWindow.setVisible(true);
                quizWindow.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);   // This will make sure the program exits when you close the window

		// 1. find an image on the internet, and put its URL in a String variable (from your browser, right click on the image, and select “Copy Image Address”)
               String URL =  "https://media1.popsugar-assets.com/files/thumbor/ePK3SGxS2LonemqWVei69oSRDr8/fit-in/2048xorig/filters:format_auto-!!-:strip_icc-!!-/2017/02/14/027/n/1922507/563d36d9ddcd740e_maxresdefault.jpg";
               Component comp;
		// 3. use the "createImage()" method below to initialize your Component
        comp=createImage(URL);
		// 4. add the image to the quiz window
        quizWindow.add(comp);
		// 5. call the pack() method on the quiz window
        quizWindow.pack();
		// 6. ask a question that relates to the image
        String answer = JOptionPane.showInputDialog("What is the answer to the math problem?");
		// 7. print "CORRECT" if the user gave the right answer
        if(answer.equalsIgnoreCase("1")) {
        	System.out.println("CORRECT");
        }else {
        	System.out.println("INCORRECT");
        }
		// 8. print "INCORRECT" if the answer is wrong

		// 9. remove the component from the quiz window (you may not see the effect of this until step 12)
        quizWindow.remove(comp);
		// 10. find another image and create it (might take more than one line of code)
        URL="data:image/jpeg;base64,";
		comp=createImage(URL);
		quizWindow.add(comp);
        // 11. add the second image to the quiz window

		// 12. pack the quiz window
        quizWindow.pack();
		// 13. ask another question
answer = JOptionPane.showInputDialog("What is the answer to the math problem?");
		// 14+ check answer, say if correct or incorrect, etc.
        if(answer.equals("102")) {
        	System.out.println("CORRECT");
        }else {
        	System.out.println("INCORRECT");
        }
	}

	private static Component createImage(String imageUrl) throws MalformedURLException {
		URL url = new URL(imageUrl);
		Icon icon = new ImageIcon(url);
		JLabel imageLabel = new JLabel(icon);
		return imageLabel;
	}

	/* OPTIONAL */
	// *14. add scoring to your quiz
	// *15. make something happen when mouse enters image (imageComponent.addMouseMotionListener()) 
}





